import React, { useState } from "react";
import { encrypt } from "@metamask/eth-sig-util";
import { Buffer } from "buffer";
import { keccak256, getBytes, Signature, BrowserProvider } from "ethers";

export default function EncryptAndSignSeparate() {
  const [message, setMessage] = useState("");
  const [ciphertextHex, setCiphertextHex] = useState("");
  const [results, setResults] = useState(null);
  const [error, setError] = useState("");

  const requestAccount = async () => {
    await window.ethereum.request({
      method: "wallet_requestPermissions",
      params: [{ eth_accounts: {} }],
    });
    const accounts = await window.ethereum.request({ method: "eth_accounts" });
    return accounts[0];
  };

  const handleEncrypt = async () => {
    setError("");
    try {
      const address = await requestAccount();
      const publicKey = await window.ethereum.request({
        method: "eth_getEncryptionPublicKey",
        params: [address],
      });

      const encryptedObject = encrypt({
        publicKey,
        data: message,
        version: "x25519-xsalsa20-poly1305",
      });

      const ciphertextBase64 = encryptedObject.ciphertext;
      const ciphertextHex = Buffer.from(ciphertextBase64, "base64").toString("hex");

      setCiphertextHex(ciphertextHex);
    } catch (err) {
      setError("Errore durante la cifratura: " + err.message);
    }
  };

  const handleSign = async () => {
    setError("");
    try {
      if (!ciphertextHex) {
        setError("Devi prima cifrare il messaggio con Alfa.");
        return;
      }

      const ciphertextBytes = Buffer.from(ciphertextHex, "hex");
      const hashHex = keccak256(ciphertextBytes);
      const hashBigInt = BigInt(hashHex);

      const provider = new BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const flatSig = await signer.signMessage(getBytes(hashHex));
      const sigObj = Signature.from(flatSig);

      setResults({
        encryptedHex: "0x" + ciphertextHex,
        hashHex,
        hashDec: hashBigInt.toString(),
        signature: flatSig,
        v: sigObj.v,
        r: sigObj.r,
        s: sigObj.s,
      });
    } catch (err) {
      setError("Errore durante la firma: " + err.message);
    }
  };

  return (
    <div style={{ padding: "1rem", fontFamily: "Arial, sans-serif" }}>
      <h2>🔐 Cifra e Firma un Messaggio</h2>
      <textarea
        placeholder="Scrivi un messaggio"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        rows={4}
        style={{ width: "100%", marginBottom: "1rem" }}
      />

      <button onClick={handleEncrypt} style={{ marginBottom: "1rem" }}>
        🔒 Cifra con Alfa (mittente)
      </button>

      <button onClick={handleSign} disabled={!ciphertextHex}>
        ✍️ Firma con Beta (relay)
      </button>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {results && (
        <div style={{ marginTop: "2rem" }}>
          <h3>📦 Risultati</h3>
          <p><strong>Messaggio cifrato (hex):</strong></p>
          <textarea readOnly value={results.encryptedHex} style={{ width: "100%", height: "80px" }} />
          <p><strong>Hash (hex):</strong> {results.hashHex}</p>
          <p><strong>Hash (decimale):</strong> {results.hashDec}</p>
          <p><strong>Firma (v, r, s):</strong></p>
          <ul>
            <li><code>v:</code> {results.v}</li>
            <li><code>r:</code> {results.r}</li>
            <li><code>s:</code> {results.s}</li>
          </ul>
        </div>
      )}
    </div>
  );
}
