import React, { useState } from "react";
import { encrypt } from "@metamask/eth-sig-util";
import { Buffer } from "buffer";
import { keccak256, getBytes, Signature, BrowserProvider } from "ethers";

export default function EncryptAndSignWithMetaMask({ setError = () => {}, onSubmit = () => {} }) {
  const [message, setMessage] = useState("");
  const [results, setResults] = useState(null);

  const handleEncryptAndSign = async (e) => {
    e.preventDefault();
    setError("");
    setResults(null);

    if (!window.ethereum) {
      setError("MetaMask non √® installato.");
      return;
    }

    try {
      const provider = new BrowserProvider(window.ethereum);
      await provider.send("eth_requestAccounts", []);
      const signer = await provider.getSigner();
      const address = await signer.getAddress();

      const publicKey = await window.ethereum.request({
        method: "eth_getEncryptionPublicKey",
        params: [address],
      });

      const encryptedObject = encrypt({
        publicKey,
        data: message,
        version: "x25519-xsalsa20-poly1305",
      });

      const ciphertextBase64 = encryptedObject.ciphertext;
      const ciphertextHex = Buffer.from(ciphertextBase64, "base64").toString("hex");
      const ciphertextBytes = Buffer.from(ciphertextBase64, "base64");

      const messageHashHex = keccak256(ciphertextBytes);
      const messageHashBigInt = BigInt(messageHashHex);

      const flatSig = await signer.signMessage(getBytes(messageHashHex));
      const sigObj = Signature.from(flatSig);

      const result = {
        encryptedHex: "0x" + ciphertextHex,
        hashHex: messageHashHex,
        hashDec: messageHashBigInt.toString(),
        signature: flatSig,
        v: sigObj.v,
        r: sigObj.r,
        s: sigObj.s,
      };

      setResults(result);
      onSubmit(result);
    } catch (err) {
      setError("Errore: " + (err.message || "Errore sconosciuto"));
    }
  };

  return (
    <div style={{ padding: "1rem", fontFamily: "Arial, sans-serif" }}>
      <h2 style={{ marginBottom: "1rem" }}>üîê Cifra e firma un messaggio con MetaMask</h2>
      <form onSubmit={handleEncryptAndSign} style={{ display: "flex", flexDirection: "column", gap: "1rem" }}>
        <textarea
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Scrivi un messaggio"
          rows={4}
          style={{ padding: "0.5rem", fontSize: "1rem", resize: "vertical" }}
        />
        <button type="submit" style={{ padding: "0.5rem 1rem", fontSize: "1rem", cursor: "pointer" }}>
          Cifra e Firma
        </button>
      </form>

      {results && (
        <div style={{ marginTop: "2rem" }}>
          <h3>üì¶ Risultati</h3>
          <div style={{ marginBottom: "1rem" }}>
            <strong>Messaggio cifrato (hex):</strong>
            <textarea readOnly value={results.encryptedHex} style={{ width: "100%", height: "80px", fontFamily: "monospace" }} />
          </div>

          <div style={{ marginBottom: "1rem" }}>
            <strong>Hash (hex):</strong>
            <textarea readOnly value={results.hashHex} style={{ width: "100%", height: "60px", fontFamily: "monospace" }} />
          </div>

          <div style={{ marginBottom: "1rem" }}>
            <strong>Hash (decimale):</strong>
            <textarea readOnly value={results.hashDec} style={{ width: "100%", height: "60px", fontFamily: "monospace" }} />
          </div>

          <div style={{ marginBottom: "1rem" }}>
            <strong>Firma (v, r, s):</strong>
            <div><code>v:</code> {results.v}</div>
            <div><code>r:</code> {results.r}</div>
            <div><code>s:</code> {results.s}</div>
          </div>
        </div>
      )}
    </div>
  );
}
