import React, { useState } from "react";
import { encrypt } from "@metamask/eth-sig-util";
import { Buffer } from "buffer";

export default function EncryptWithMetaMask({ setError = () => {} }) {
  const [message, setMessage] = useState("");
  const [encryptedData, setEncryptedData] = useState("");

  const handleEncrypt = async (e) => {
    e.preventDefault();
    setError("");
    setEncryptedData("");

    if (!window.ethereum) {
      setError("MetaMask non è installato.");
      return;
    }

    try {
      // 1. Chiedi accesso a MetaMask
      const accounts = await window.ethereum.request({ method: "eth_requestAccounts" });
      const address = accounts[0];

      // 2. Ottieni la chiave pubblica di cifratura
      const publicKey = await window.ethereum.request({
        method: "eth_getEncryptionPublicKey",
        params: [address],
      });

      // 3. Cifra il messaggio
      const encryptedObject = encrypt({
        publicKey,
        data: message,
        version: "x25519-xsalsa20-poly1305",
      });

      // 4. Estrai solo il campo ciphertext, converti da base64 a hex
      const ciphertextBase64 = encryptedObject.ciphertext;
      const ciphertextHex = Buffer.from(ciphertextBase64, "base64").toString("hex");

      // 5. Salva l'esadecimale nello stato
      setEncryptedData("0x" + ciphertextHex);
    } catch (err) {
      setError("Errore: " + (err.message || "Errore sconosciuto"));
    }
  };

  return (
    <div>
      <form onSubmit={handleEncrypt}>
        <textarea
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Scrivi un messaggio"
        />
        <button type="submit">Cifra</button>
      </form>

      {encryptedData && (
        <div>
          <p><strong>Messaggio cifrato (esadecimale):</strong></p>
          <textarea
            readOnly
            value={encryptedData}
            style={{ width: "100%", height: "200px", fontFamily: "monospace" }}
          />
        </div>
      )}
    </div>
  );
}
