import React, { useState } from "react";
import { encrypt } from "@metamask/eth-sig-util";
import { Buffer } from "buffer";
import { keccak256, getBytes, Signature, BrowserProvider } from "ethers";

export default function EncryptAndSignSeparate() {
  const [message, setMessage] = useState("");
  const [ciphertextHex, setCiphertextHex] = useState("");
  const [hashHex, setHashHex] = useState("");
  const [hashDec, setHashDec] = useState("");
  const [results, setResults] = useState(null);
  const [error, setError] = useState("");

  const requestAccount = async () => {
    await window.ethereum.request({
      method: "wallet_requestPermissions",
      params: [{ eth_accounts: {} }],
    });
    const accounts = await window.ethereum.request({ method: "eth_accounts" });
    return accounts[0];
  };

  const handleEncrypt = async () => {
    setError("");
    setResults(null);
    try {
      const address = await requestAccount();
      const publicKey = await window.ethereum.request({
        method: "eth_getEncryptionPublicKey",
        params: [address],
      });

      const encryptedObject = encrypt({
        publicKey,
        data: message,
        version: "x25519-xsalsa20-poly1305",
      });

      const ciphertextBase64 = encryptedObject.ciphertext;
      const hex = Buffer.from(ciphertextBase64, "base64").toString("hex");

      const ciphertextBytes = Buffer.from(hex, "hex");
      const hash = keccak256(ciphertextBytes);
      const hashBigInt = BigInt(hash);

      setCiphertextHex(hex);
      setHashHex(hash);
      setHashDec(hashBigInt.toString());
    } catch (err) {
      setError("Errore durante la cifratura: " + err.message);
    }
  };

  const handleSign = async () => {
    setError("");
    try {
      const address = await requestAccount();
      const provider = new BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      //questa riga dice a MetaMask (tramite EIP-191) di firmare il messaggio 7
      // (in questo caso, l‚Äôhash del messaggio cifrato) usando la chiave privata dell‚Äôaccount attivo.
      const flatSig = await signer.signMessage(getBytes(hashHex));
      //Il risultato (flatSig) √® una firma compatta in formato concatenato: contiene al suo interno 
      // i tre componenti r, s, e v, codificati insieme.
      const sigObj = Signature.from(flatSig);

      setResults({
        //formato compatto della firma
        signature: flatSig,
        v: sigObj.v,
        r: sigObj.r,
        s: sigObj.s,
        betaAddress: address,
      });
    } catch (err) {
      setError("Errore durante la firma: " + err.message);
    }
  };

  return (
    <div style={{ padding: "1rem", fontFamily: "Arial, sans-serif" }}>
      <h2>üîê Cifra e Firma un Messaggio</h2>

      <textarea
        placeholder="Scrivi un messaggio"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        rows={4}
        style={{ width: "100%", marginBottom: "1rem" }}
      />

      <button onClick={handleEncrypt} style={{ marginBottom: "1rem" }}>
         Cifra con Alfa (mittente)
      </button>

      {ciphertextHex && (
        <div style={{ marginTop: "1rem" }}>
          <h3> Dati Cifrati</h3>
          <p><strong>Messaggio cifrato (hex):</strong></p>
          <textarea readOnly value={"0x" + ciphertextHex} style={{ width: "100%", height: "80px" }} />
          <p><strong>Hash (hex):</strong> {hashHex}</p>
          <p><strong>Hash (decimale):</strong> {hashDec}</p>

          <button onClick={handleSign} style={{ marginTop: "1rem" }}>
             Firma con Beta (relay)
          </button>
        </div>
      )}

      {results && (
        <div style={{ marginTop: "2rem" }}>
          <h3> Firma</h3>
          <ul>
            <li><code>v:</code> {results.v}</li>
            <li><code>r:</code> {results.r}</li>
            <li><code>s:</code> {results.s}</li>
            <li><code>Beta address:</code> {results.betaAddress}</li>
          </ul>
        </div>
      )}

      {error && <p style={{ color: "red" }}>{error}</p>}
    </div>
  );
}
